import React from 'react';
import { storiesOf } from '@storybook/react';
import { Button } from '@storybook/react/demo';
import { Table, Input, Select, Checkbox } from 'antd';
import { Z_BLOCK } from 'zlib';




const dataSource = [{
  key: '1',
  name: 'Mike',
  age: 32,
  address: '10 Downing Street'
}, {
  key: '2',
  name: 'John',
  age: 42,
  address: '10 Downing Street'
}];

const columns = [{
  title: 'Name',
  dataIndex: 'name',
  key: 'name',
}, {
  title: 'Age',
  dataIndex: 'age',
  key: 'age',
}, {
  title: 'Address',
  dataIndex: 'address',
  key: 'address',
}];

storiesOf('Button', module)
  .add('with text', () => (
    <Button>Hello Button</Button>
  ))
  .add('with some emoji', () => (
    <Button><span role="img" aria-label="so cool">😀 😎 👍 💯</span></Button>
  ));




storiesOf('Table', module)
  .add('with table', () => (
    <Table dataSource={dataSource} columns={columns} />
  ));


const Option = Select.Option;
const { TextArea } = Input;

function handleChange(value) {
  console.log(`selected ${value}`);
}

function handleBlur() {
  console.log('blur');
}

function handleFocus() {
  console.log('focus');
}






storiesOf('Query Repository', module)
  .add('repository', () => (
    <table cellSpacing={0}>
      <tbody>
        <tr>
          <th>
            <text>Query Language:</text>
          </th>
          <td>
            <Select
              showSearch
              style={{ width: 200 }}
              placeholder="Select a language"
              optionFilterProp=""
              onChange={handleChange}
              onFocus={handleFocus}
              onBlur={handleBlur}
              filterOption={(input, option) => option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
            >
              <Option value="sdfs">Jack</Option>
              <Option value="dfsd">Lucy</Option>
            </Select>
          </td>
        </tr>
        <tr>
          <th>
            <text>Query:</text>
          </th>
          <td>
            <TextArea cols={100} rows={4} ></TextArea>
          </td>
        </tr>
        <tr>
          <th>
            <text>Result per page:</text>
          </th>
          <td>
            <Select
              showSearch
              style={{ width: 100 }}
              optionFilterProp=""
              onChange={handleChange}
              onBlur={handleBlur}
              filterOption={(input, option) => option.props.children.toLowerCase().indexOf(input.toLowerCase()) >= 0}
            >
              <Option value="first" selected>Jac5456</Option>
              <Option value="second">Lucy</Option>
            </Select>
          </td>
        </tr>
        <tr>
          <td>
            <text>Actions Options:</text>
          </td>
          <td>
            <Checkbox checked="checked" >Include inferred statements</Checkbox>
          </td>
          <td>
            <Checkbox>Save privately(do not share)</Checkbox>
          </td>
        </tr>
        <tr>
          <th>
            <text>Actions:</text>
          </th>
          <th>
            <div style={{ display: 'inline' }}>
              <div><Button>Clear</Button></div>
              <div><Button>Execute</Button></div>
              <div><Button>Save query</Button></div>
              <div><TextArea cols={10} rows={1}></TextArea></div>
            </div>
          </th>
        </tr>
      </tbody >
    </table >

  ));
